var product_mutil={initController:function(){product_mutil.showDetails()},showDetails:function(){$(".delete-all-show").on("click",function(){var t=[];$(".checkbox:checked").each(function(){t.push($(this).val())}),$.ajax({url:"/admin/product/multiple/"+t,type:"GET",contentType:"application/json; charset=utf-8",dataType:"JSON",async:!1,success:function(t){var e="",o=$("#data-template-order").html();$.each(t,function(t,r){e+=Mustache.render(o,{ID:r.id,NAME:r.user_name,ADDRESS:r.address,PHONE:r.phone,EMAIL:r.user.email,DATETIME:product_mutil.formatDateTime(r.created_at),PRICE:product_mutil.formatPrice(r.total_price)+" \u0111"})}),$("tbody#viewLoadOrder").html(e)},error:function(t){console.log(t)}})})},formatPrice:function(t){return t.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g,"$1,")},formatDateTime:function(t){var e=new Date(t);return dateTime=moment(e).format("DD/MM/YYYY"),dateTime}};$(document).ready(function(){product_mutil.initController()});